"use strict";var element=document.querySelector(".element");console.log(element);var button=document.querySelector(".button");function onClick(){"function"==typeof DeviceOrientationEvent.requestPermission?DeviceOrientationEvent.requestPermission().then(function(e){"granted"===e&&window.addEventListener("deviceorientation",function(e){element.style.transform="rotateZ("+(e.alpha-180)+"deg) rotateX("+e.beta+"deg) rotateY("+-e.gamma+"deg)"})}).catch(console.error):document.body.style.background="brown"}button.addEventListener("click",onClick);